import React from 'react'
import Carousel from "react-material-ui-carousel"
import { Link, Typography } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'
import SliderImage from '../images/slider1.jpg'
import Scroll from "react-scroll"
const ScrollLink = Scroll.ScrollLink

const useStyles = makeStyles({
    image: {
      width: "100%",
      height: "600px",
      objectFit: "cover",
      objectPosition: "50% 40%"
    },
    carousel: {
      marginTop: "10",
    },
    slide: {
        position: "relative"
    },
    title: {
        position: "absolute",
        top: "70px",
        width: "100%",
        fontSize: "30px",
        fontFamily: "BigCaslonMedium",
        color: "#6a6a6a"
    }
});

scrollToSection = (className) => {
    scroller.scrollTo(className, {
        duration: 800,
        delay: 0,
        smooth: "easeInOutQuart",
    });
};
export default function HomeSlider() {
    const classes = useStyles();
    return (
        <div>
            <Carousel
                autoplay={true}
                animation="slide"
                indicators={true}
                navButtonsAlwaysVisible={false}
                cycleNavigation={true}
                navButtonsProps={{
                    style: {
                        background: "#fff",
                        color: "#494949",
                        borderRadius: 0,
                        margin: 0,
                    },
                }}
                className={classes.carousel}
            >
                <div className={classes.slide}>
                    <img src={SliderImage} alt="" className={classes.image} />
                    <Typography className={classes.title}>
                        Super Soft, Stylish Bed Linen<br></br>
                        Made With Love
                    </Typography>
                </div>
                <div className={classes.slide}>
                    <img src={SliderImage} alt="" className={classes.image} />
                    <Typography className={classes.title}>
                        Super Soft, Stylish Bed Linen<br></br>
                        Made With Love
                    </Typography>
                </div>
            </Carousel>
            <ScrollLink 
                to="#home" 
                spy={true} 
                smooth={true} 
                duration={500} 
                className={classes.ScrollLink}
            >
                Explore
            </ScrollLink>
        </div>
    );
}
