import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import VizSensor from 'react-visibility-sensor'
import { Fade, Button, Slide } from '@material-ui/core';

const useStyles = makeStyles({
    ShopBox: {
        backgroundColor: "black",
        width: "100%",
        height: "550px",
        position: "relative",

    },
    ShopImage: {
        width: "100%",
        height: "100%",
        objectFit: "cover"
    },
    ShopBtn: {
        backgroundColor: "white",
        color: "#6a6a6a",
        fontFamily: "BrownStdBold",
        fontSize: "14px",
        width: "50%",
        position: "absolute",
        bottom: "80px",
        borderRadius: "0",
        zIndex: 1,
        left: "25%",
        textTransform: "capitalize"
    },
});

export default function ShopCategory(props) {
    const classes = useStyles();
    let [active, setActive] = useState(false);
    return (
        <VizSensor
            onChange={(isVisible) => {
                if(!active){
                    setActive(isVisible);
                }
            }}
        >
            <Slide in={active} direction="up" timeout={1000}>
                <div className={classes.ShopBox}>
                    <img src={props.image} alt="" className={classes.ShopImage} />
                    <Slide in={active} direction="up" timeout={1000}>
                        <Button variant="contained" className={classes.ShopBtn}>{props.name}</Button>
                        </Slide>
                </div>
            </Slide>
        </VizSensor>
    )
}
